INSERT INTO kpi.authority (id, name, top_level) VALUES (403, '5.8.18 Обеспечению подготовки предложений о применении предусмотренных законодательством Российской Федерации мер ограничительного, предупредительного и профилактического характера, направленных на недопущение и (или) пресечение нарушений юридическими лицами и физическими лицами обязательных требований в сфере государственного регулирования (цен) тарифов в области электроэнергетики. ', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (404, '5.8.21.Обеспечению подготовки предложений о направлении физическим и юридическим лицам обязательных для исполнения предписаний о прекращении нарушений законодательства о государственном регулировании тарифов, выявленных по результатам проведения плановых и внеплановых проверок юридических лиц и индивидуальных предпринимателей в пределах компетенции курируемых структурных подразделений.', false);
