INSERT INTO kpi.authority (id, name, top_level) VALUES (622, '2.2. Мониторинг государственных программ «Безопасный город», «Развитие городской среды», «Развитие коммунально-инженерной инфраструктуры и энергосбережение» с использованием информации, вносимой в ИАС МКР и АСУ ГФ, подготовка заключений по годовым отчетам по выполнению государственных программ. ', false);
