INSERT INTO kpi.authority (id, name, top_level) VALUES (750, '2.1.2. Установление ставок платы за технологическое присоединение к электрическим сетям (также в индивидуальном порядке).', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (751, '2.1.9. Участие в проведении проверок хозяйственной деятельности организаций, осуществляющих деятельность в сфере регулируемого ценообразования, в части обоснованности величины указанных цен (тарифов) и правильности их применения;', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (752, '2.1.10. Осуществление мониторинга уровня регулируемых цен (тарифов) и влияющих на их изменение факторов, а также уровня нерегулируемых цен на электрическую энергию (мощность) в порядке, установленном Правительством Российской Федерации;', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (753, '2.1.3. Установление долгосрочных параметров регулирования для организаций, в отношении которых действующим законодательством предусмотрено долгосрочное регулирование.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (754, '2.1.4. Установление нормы доходности капитала, инвестированного до перехода к регулированию с применением метода доходности инвестированного капитала, в отношении сетевых организаций.', false);
