INSERT INTO kpi.authority (id, name, top_level) VALUES (758, '2.1.22. Направление в Федеральную антимонопольную службу России информации по Единой информационно-аналитической системе (далее - ЕИАС) по вопросам, входящим в компетенцию Управления.', false);
