INSERT INTO kpi.authority (id, name, top_level) VALUES (769, '2.1.12. Согласование с Федеральной антимонопольной службой России решений об установлении тарифов на уровне, выше предельного максимального или ниже предельного минимального уровней;', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (771, '2.1.11. Согласование предложений, касающихся перехода к регулированию тарифов с применением метода доходности инвестированного капитала с Федеральной антимонопольной службой России;', false);
