INSERT INTO kpi.authority (id, name, top_level) VALUES (788, 'Участие в разработке прогнозов социально-экономического развития города по отраслям: «Транспорт, связь, информатика» на основе подпрограмм «Общественный транспорт «Метрополитен», «Общественный транспорт «Наземный городской пассажирский транспорт», «Общественный транспорт «Железнодорожный транспорт», «Автовокзалы и транспортно-пересадочные узлы (ТПУ)» Государственной программы города Москвы «Развитие транспортной системы», «Дорожное хозяйство» на основе подпрограммы «Автомобильные дороги и улично-дорожная сеть» Государственной программы города Москвы «Развитие транспортной системы», «Жилищное строительство» на основе подпрограммы «Строительство нового жилья в городе Москве и реновация существующей жилой застройки» Государственной программы города Москвы «Жилище», (далее - курируемые отрасли) на очередной финансовый год, среднесрочную и долгосрочную перспективу на основе базовых показателей.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (789, 'Участие в подготовке проектов правовых актов Правительства Москвы по курируемым направлениям деятельности.', false);
