INSERT INTO kpi.authority (id, name, top_level) VALUES (791, 'Формирование показателей АИП с учетом обоснованности затрат, подготовка заключения об эффективности использования средств бюджета города Москвы, направляемых на проектирование, строительство, реконструкцию и техническое перевооружение объектов АИП по курируемым отраслям.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (792, 'Осуществление формирования, выпуска и внесения изменений в титульные списки проектно-изыскательских работ и строек по объектам АИП курируемых отраслей и государственных программ города Москвы.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (793, 'Осуществление анализа и контроля выполнения АИП, участие в подготовке предложений по ее корректировке.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (794, 'Осуществление подготовки предложений по распределению бюджетных ассигнований по главным распорядителям средств бюджета города Москвы, государственным программам города Москвы, их подпрограммам и мероприятиям по курируемым отраслям.', false);
