INSERT INTO kpi.authority (id, name, top_level) VALUES (796, 'Подготовка материалов к совещаниям и другим мероприятиям по курируемым отраслям.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (797, 'Подготовка материалов, справок по выполнению контрольных поручений руководства.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (798, 'Обеспечение своевременного выполнения поручений руководства Департамента экономической политики и развития города Москвы по курируемым отраслям.', false);
