INSERT INTO kpi.authority (id, name, top_level) VALUES (800, 'Обеспечение своевременного рассмотрения запросов представителей законодательной власти, обращений граждан и юридических лиц и принятие меры по решению поставленных вопросов в пределах своей компетенции.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (801, 'Участие в подготовке справочных, аналитических и информационных материалов по курируемым отраслям.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (802, 'Осуществление подготовки материалов для их предоставления по запросам контролирующих органов Российской Федерации и города Москвы.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (803, 'Осуществление мониторинга законодательно-нормативной базы в сфере строительства и проектирования объектов курируемых отраслей.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (804, 'Рассмотрение предложений государственных заказчиков по выделению средств городского бюджета на проведение работ по проектированию и строительству объектов в части наличия обоснования, утвержденной проектно­сметной документации и соблюдения конкурсных процедур.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (805, 'Подготовка заключений к технологическим и техническим заданиям на проектирование объектов капитального строительства государственного заказа города Москвы, а также объектов, включенных в План деятельности Московского фонда реновации жилой застройки.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (806, 'Формирование повестки личных докладов у заместителя Мэра Москвы в Правительстве Москвы по вопросам экономической политики и имущественно-земельных отношений, совещаний по Комплексу экономической политики и имущественно-земельных отношений у Мэра Москвы, оперативных совещаний по Комплексу экономической политики и имущественно-земельных отношений, в том числе с учётом имеющихся поручений в системе Электронного документооборота (ЭДО), Электронного офиса руководителя (ЭОР), папке особого контроля (КП), отдельных поручений руководителя ', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (807, 'Участие в совещаниях руководителя Департамента и оформление проектов протоколов (поручений) по их итогам', false);
