INSERT INTO kpi.authority (id, name, top_level) VALUES (809, 'Должностной Регламент Попковой А.М.(начальник отдела цен и тарифной политики в городском хозяйстве)2.2.7.7. Выполнение отдельных поручений начальника Управления и заместителя начальника Управления по вопросам, относящимся к компетенции Отдела.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (812, 'Должностной Регламент Попковой А.М.(начальник отдела цен и тарифной политики в городском хозяйстве)2.2.7.1. Разработка проектов правовых актов Мэра Москвы, Правительства Москвы, Департамента по отдельным вопросам, входящим в компетенцию Отдела.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (813, 'Должностной Регламент Попковой А.М.(начальник отдела цен и тарифной политики в городском хозяйстве) 2.2.7.2. Рассмотрение и анализ предложений органов исполнительной власти по проектам цен, тарифов, расценок, торговых надбавок, наценок и других нормативов затрат, подлежащих регулированию и согласованию в соответствии с законодательством Российской Федерации и города Москвы, на:1) эксплуатацию конструкций мостовых сооружений, используемых для прокладки инженерных коммуникаций; 2) эксплуатацию коммуникационных коллекторов, находящихся на балансе ГУП «Москоллектор»; 3) размещение оборудования и кабельных линий в тоннелях и коллекторах ГУП «Московский метрополитен» при предоставлении аренды без проведения конкурса; 4)предельные размеры оптовых и предельные размеры розничных надбавок к фактическим отпускным ценам, установленным производителями лекарственных препаратов, на лекарственные препараты, включенные в перечень жизненно необходимых и важнейших лекарственных препаратов; 5)работы по отлову, содержанию, стерилизации и учету безнадзорных и бродячих животных; 6)ритуальные услуги, оказываемые за счет средств бюджета города Москвы.', false);
