INSERT INTO kpi.authority (id, name, top_level) VALUES (898, 'Осуществление мониторинга представленных отчетов о реализации мероприятий приоритетных проектов: «Образование», «Здравоохранение», «Доступное и комфортное жилье - гражданам России» и мероприятий по улучшению демографической ситуации в городе Москве, а также передача их в адрес федеральных органов исполнительной власти.', false);
