INSERT INTO kpi.authority (id, name, top_level) VALUES (900, 'Осуществление анализа экономической эффективности государственных программ города Москвы и подготовка предложений по их корректировке совместно со структурными подразделениями Департамента.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (901, 'Подготовка сводного годового доклада о выполнении государственных программ города Москвы.', false);
