INSERT INTO kpi.authority (id, name, top_level) VALUES (912, 'Подготовка результатов оценки эффективности предоставляемых (планируемых к предоставлению) налоговых льгот и ставок налогов, установленных законами города Москвы.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (913, 'Подготовка предложений и проектов законодательных инициатив города о внесении изменений в федеральное налоговое и бюджетное законодательство по вопросам в установленной сфере деятельности.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (915, 'Подготовка проектов законов города Москвы в сфере налоговой политики.', false);
