INSERT INTO kpi.authority (id, name, top_level) VALUES (1003, 'Реализация по итогам экономического анализа и планирования хозяйственной деятельности регулируемых организаций процедуры согласования стоимости абонементных билетов для населения на услуги по железнодорожным перевозкам пассажиров в пригородном сообщении (в пределах города Москвы), осуществляемые субъектами естественных монополий, в рамках работы межведомственных и межрегиональных рабочих групп, а также Комиссии по тарифному регулированию Объединенной коллегии исполнительных органов государственной власти Москвы и Московской области.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (1004, 'Реализация по итогам экономического анализа и планирования хозяйственной деятельности регулируемых организаций процедуры подготовки и внесения в установленном порядке на рассмотрение руководства ДЭПиР предложений по установлению экономически обоснованных цен (тарифов) на услуги по железнодорожным перевозкам пассажиров в пригородном сообщении (в пределах города Москвы), осуществляемые субъектами естественных монополий.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (1005, 'Реализация по итогам экономического анализа и планирования хозяйственной деятельности регулируемых организаций процедуры подготовки и внесения в установленном порядке на рассмотрение руководства ДЭПиР предложений по государственному регулированию (установлению, пересмотру) цен (тарифов) на транспортные услуги, оказываемые на подъездных железнодорожных путях организациями промышленного железнодорожного транспорта и другими хозяйствующими субъектами независимо от организационно-правовой формы, за исключением организаций федерального железнодорожного транспорта.', false);
INSERT INTO kpi.authority (id, name, top_level) VALUES (1006, 'Реализация по итогам экономического анализа и планирования хозяйственной деятельности регулируемых организаций процедуры подготовки и внесения в установленном порядке на рассмотрение руководства ДЭПиР предложений по государственному регулированию (установлению, пересмотру) цен (тарифов) на услуги в речных портах, оказываемые субъектами естественных монополий, включенными в реестр субъектов естественных монополий и не включенными в перечень субъектов естественных монополий в сфере услуг в речных портах, государственное регулирование которых осуществляется уполномоченным федеральным органом исполнительной власти.', false);
