INSERT INTO kpi.authority (id, name, top_level) VALUES (474, '2.2.17.Участвует в рассмотрении нормативных затрат на оказание (выполнение) государственных услуг (работ) и содержание имущества государственных учреждений города Москвы, подведомственных Департаменту, в рамках государственного задания для выделения субсидий, а также в рассмотрении обосновывающих материалов для выделения целевых субсидий для данных учреждений.', true);
