INSERT INTO kpi.authority (id, name, top_level) VALUES (485, '2.2.6. Организует разработку базы стоимостных нормативов по эксплуатации зданий и сооружений, содержанию памятников культурного наследия, праздничному и тематическому оформлению города Москвы для определения размера субсидий, для расчета тарифов, для оценки сметной стоимости работ экспертными организациями, осуществляющими проведение проверки (экспертизы) достоверности определения начальной (максимальной) цены контрактов (договоров). ', true);
