INSERT INTO kpi.authority (id, name, top_level) VALUES (504, '2.1.13. Участие в оценке эффективности использования целевых субсидий, предоставленных государственным унитарным предприятиям (государственным предприятиям, казенным учреждениям) города Москвы в рамках Адресной инвестиционной программы города Москвы в соответствии с постановлением от 28.01.2014 № 28-ПП.', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (505, '2.1.21. Подготовка материалов к совещаниям и другим мероприятиям по курируемым отраслям.', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (506, '2.1.27. Подготовка материалов, справок по выполнению контрольных поручений руководства.', true);
