INSERT INTO kpi.authority (id, name, top_level) VALUES (751, '2.1.9. Участие в проведении проверок хозяйственной деятельности организаций, осуществляющих деятельность в сфере регулируемого ценообразования, в части обоснованности величины указанных цен (тарифов) и правильности их применения;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (752, '2.1.10. Осуществление мониторинга уровня регулируемых цен (тарифов) и влияющих на их изменение факторов, а также уровня нерегулируемых цен на электрическую энергию (мощность) в порядке, установленном Правительством Российской Федерации;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (753, '2.1.3. Установление долгосрочных параметров регулирования для организаций, в отношении которых действующим законодательством предусмотрено долгосрочное регулирование;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (754, '2.1.4. Установление нормы доходности капитала, инвестированного до перехода к регулированию с применением метода доходности инвестированного капитала, в отношении сетевых организаций;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (755, '2.1.13. Участие в формировании сводного прогнозного баланса производства и поставок электрической энергии (мощности) в Московском регионе;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (756, '2.1.25. Рассмотрение обращений и запросов федеральных органов исполнительной власти и органов власти субъектов Российской Федерации, а также учреждений, организаций и граждан по вопросам, относящимся к компетенции Департамента, в рамках полномочий Управления', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (757, '2.1.10. Осуществление мониторинга уровня регулируемых цен (тарифов) и влияющих на их изменение факторов, а также уровня нерегулируемых цен на электрическую энергию (мощность) в порядке, установленном Правительством Российской Федерации', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (758, '2.1.22. Направление в Федеральную антимонопольную службу России информации по Единой информационно-аналитической системе (далее - ЕИАС) по вопросам, входящим в компетенцию Управления;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (759, '2.1.23. Внесение информации в государственную информационную систему жилищно-коммунального хозяйства (ГИС ЖКХ) о размерах стандарта стоимости жилищно-коммунальных услуг, применяемого для расчета субсидий на оплату жилого помещения и коммунальных услуг, а также о поступающих от пользователей ГИС ЖКХ обращениях и результатах их рассмотрения по вопросам, входящим в компетенцию Управления.', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (760, '2.1.15. Подготовка предложений и заключений по проектам законов и иных правовых актов города Москвы и Правительства Москвы по вопросам, связанным с компетенцией Управления;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (761, '2.1.18. Подготовка предложений и обращений в органы исполнительной и законодательной власти города Москвы и федеральные органы по вопросам, входящим в компетенцию Управления;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (762, '2.1.7. Участие в рассмотрении инвестиционных программ, программ энергосбережения и повышения энергоэффективности, производственных показателей регулируемых организаций по запросам, курирующего данные направления Управления;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (763, '2.1.26. Иные задачи и функции, отнесенные к компетенции Управления, исполняемые по поручению руководителя Департамента и заместителя Департамента.', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (764, '2.1.25. Рассмотрение обращений и запросов федеральных органов исполнительной власти и органов власти субъектов Российской Федерации, а также учреждений, организаций и граждан по вопросам, относящимся к компетенции Департамента, в рамках полномочий Управления;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (765, '2.1.14. Формирование и сопровождение деятельности рабочих групп для рассмотрения вопросов, относящихся к компетенции Управления;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (766, '2.1.17. Подготовка материалов к совещаниям, консультациям, выступлениям и другим мероприятиям по вопросам, входящим в компетенцию Управления;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (767, '2.1.24. Представление интересов Департамента в федеральных органах исполнительной власти, органах исполнительной власти субъектов Российской Федерации, органах местного самоуправления и организациях по вопросам, отнесенным к компетенции Управления;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (768, '2.1.16. Участие в совершенствовании методологической базы по расчету тарифов (цен) на продукцию (услуги) и платы за технологическое присоединение к электрическим сетям в рамках компетенции Управления;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (769, '2.1.12. Согласование с Федеральной антимонопольной службой России решений об установлении тарифов на уровне, выше предельного максимального или ниже предельного минимального уровней;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (770, '2.1.5. Установление показателей качества и надежности поставляемых товаров и оказываемых услуг регулируемых организаций;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (771, '2.1.11. Согласование предложений, касающихся перехода к регулированию тарифов с применением метода доходности инвестированного капитала с Федеральной антимонопольной службой России;', true);
INSERT INTO kpi.authority (id, name, top_level) VALUES (772, '2.1.36. Участие в проведении проверок хозяйственной деятельности организаций, осуществляющих деятельность в сфере регулируемого ценообразования, в части вопросов, относящихся к компетенции Управления.', null);
INSERT INTO kpi.authority (id, name, top_level) VALUES (773, '2.1.35. Мониторинг исполнения программ в области энергосбережения и повышения энергетической эффективности регулируемых организаций города Москвы.', null);
INSERT INTO kpi.authority (id, name, top_level) VALUES (774, '2.1.34. Мониторинг исполнения инвестиционных (производственных) программ и программ газификации регулируемых организаций города Москвы.', null);
INSERT INTO kpi.authority (id, name, top_level) VALUES (775, '2.1.41. Проведение анализа производственных показателей деятельности регулируемых организаций в рамках компетенции Управления.', null);
